mapper.insert:
UPDATE ykb_user SET
id = #{id} , name = #{name}, gender = #{gender}, age = #{age}, created_by = #{createdBy}, created_date = #{createdDate}
, updated_date = #{updatedDate}, updated_by =  #{updatedBy}

mapper.select:
SELECT u.* FROM ykb_user u
and u.id = #{id}
and name like '%${name}%'
and gender = #{gender}
ORDER BY u.created_date DESC
